@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent {
  formName="User Form";
  val: number=9;
  Save()
  {
    alert('Form submitted'+this.firstName+' ' +this.lastName);
  }
  firstName:string ="Tushar";
  lastName:string ="Misal";
}
