import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent implements OnInit {

  formName: string= "User Detail Form";
  val: number = 9;
  firstName: string = '';
  lastName: string = '';

  constructor() { }

  ngOnInit(): void {
  }

  Save(){
    alert('Form Submitted..! ' + 'FirstName ' + this.firstName + " LastName :" + this.lastName);
  }

  ResetForm(){
    this.firstName = '';
    this.lastName = '';
  }

}
